-- ----------------------------
-- Table structure for fjznny_article
-- ----------------------------
DROP TABLE IF EXISTS `fjznny_article`;
CREATE TABLE `fjznny_article` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `classid` int(11) NOT NULL DEFAULT '0',
  `topic` varchar(255) NOT NULL DEFAULT '',
  `keyword` varchar(255) NOT NULL,
  `author` varchar(255) NOT NULL,
  `source` varchar(255) NOT NULL,
  `infoimg` varchar(255) NOT NULL DEFAULT '',
  `infoimg2` varchar(255) NOT NULL,
  `infointro` text NOT NULL,
  `content` text NOT NULL,
  `content1` text NOT NULL,
  `content2` text NOT NULL,
  `content3` text NOT NULL,
  `content4` text NOT NULL,
  `joindate` int(11) NOT NULL DEFAULT '0',
  `sort_order` int(11) NOT NULL DEFAULT '0',
  `isgood` tinyint(4) NOT NULL DEFAULT '0',
  `hitnum` int(11) NOT NULL DEFAULT '0',
  PRIMARY KEY (`id`)
) ENGINE=MyISAM AUTO_INCREMENT=109 DEFAULT CHARSET=utf8;

