INSERT INTO `fjznny_article` VALUES ('61', '16', '土壤监测仪', '', '', '', '20140511102233_841.jpg', '', '土壤监测仪', '<p style=\"text-align: center; \">&nbsp;</p>\r\n<p style=\"text-align: center; \">&nbsp;</p>\r\n<p style=\"text-align: center; \">&nbsp;<img src=\"/upfiles/contentfiles/image/201405/20140511112138100.jpg\" width=\"283\" height=\"220\" alt=\"\" /></p>', '', '', '', '', '1399774919', '4', '1', '2');
