INSERT INTO `fjznny_article` VALUES ('107', '34', '天津绿圣蓬源农业科技开发有限公司', '', '', '', '20140512145629_735.jpg', '', '天津绿圣蓬源农业科技开发有限公司', '<p style=\"text-align: center; \">&nbsp;<img src=\"/upfiles/contentfiles/image/201405/20140512145435130.jpg\" width=\"480\" height=\"360\" alt=\"\" /></p>\r\n<p style=\"text-align: center; \">&nbsp;</p>\r\n<p style=\"text-align: left; \">\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;line-height:18.0pt;background:white\"><span style=\"font-size: 14px; \"><span style=\"font-family: 宋体; \"><span lang=\"EN-US\" style=\"color: rgb(102, 102, 102); \">&nbsp; &nbsp;&nbsp;</span><span style=\"color: rgb(102, 102, 102); \">天津绿圣蓬源农业科技开发有限公司坐落在北辰区双街现代农业科技园内，公司以工厂化生产蟹味菇为主，是一个集科研、技术培训、开发、加工为一体的现代化高效环保、高科技生产的贸易型企业。</span></span></span><span lang=\"EN-US\" style=\"font-family:&quot;Verdana&quot;,&quot;sans-serif&quot;;color:#666666\"><o:p></o:p></span></p>\r\n<p style=\"margin: 0cm 0cm 0.0001pt; line-height: 18pt; background-color: white; background-position: initial initial; background-repeat: initial initial; \"><span style=\"font-size: 14px; \"><span style=\"font-family: 宋体; \"><span lang=\"EN-US\" style=\"color: rgb(102, 102, 102); \">&nbsp; &nbsp;&nbsp;</span><span style=\"color: rgb(99, 99, 99); background-color: white; \">公司总投资<span lang=\"EN-US\">1.1</span>亿元，总建筑面积<span lang=\"EN-US\">35000</span>㎡，生产设备、生产所用菌种及菌种培养技术均从日本引进，</span><span style=\"color: rgb(102, 102, 102); \">公司采取先进生产设备及生产技术，从菌种培养到包装贮藏整个过程全部使用自动化设备及机械手完成，</span><span style=\"color: rgb(99, 99, 99); background-color: white; \">年产量<span lang=\"EN-US\">7000</span>吨，实现年销售收入<span lang=\"EN-US\">1.4</span>亿元，利润在<span lang=\"EN-US\">30%</span>左右，</span><span style=\"color: rgb(102, 102, 102); \">产品主要销售京津地区，远销欧美市场。公司通过推广及技术合作，掌握国内一流的蟹味菇栽培生产技术和先进的管理理念，增强公司的整体实力，提升市场竞争力，生产技术达到国际领先水平，生产规模将排在国内前列。</span></span></span><span lang=\"EN-US\" style=\"font-family:&quot;Verdana&quot;,&quot;sans-serif&quot;;color:#666666\"><o:p></o:p></span></p>\r\n<p style=\"margin: 0cm 0cm 0.0001pt; line-height: 18pt; background-color: white; background-position: initial initial; background-repeat: initial initial; \"><span style=\"font-size: 14px; \"><span style=\"font-family: 宋体; \"><span lang=\"EN-US\" style=\"color: rgb(102, 102, 102); \">&nbsp;&nbsp;&nbsp; </span><span style=\"color: rgb(102, 102, 102); \">公司倡导社会责任意识，积极探索出菇后基质循环利用技术。为当地设施蔬菜种植基地改良土壤提供原料，同时收购周边地区的玉米芯、麸皮、棉花秆等农业秸秆资源，带动农户致富。公司计划利用现有优良设施条件和技术力量，生产各类别、各级别菌种，提供给周边菇农，带动菇农致富。逐步实现公司加农户的方式，发挥企业核心竞争力，在市场开拓、生产组织、质量控制、产品收购、价格调节上发挥作用，成为我市食用菌行业龙头企业。</span></span></span><span lang=\"EN-US\" style=\"font-family:&quot;Verdana&quot;,&quot;sans-serif&quot;;color:#666666\"><o:p></o:p></span></p>\r\n<span style=\"font-size: 14px; \"><span style=\"font-family: 宋体; \">  <span lang=\"EN-US\" style=\"color: rgb(102, 102, 102); \">&nbsp;&nbsp;&nbsp; </span><span style=\"color: rgb(102, 102, 102); \">公司在稳定和提高现有主打产品基础上，依托自身的人才储备和硬件设施，将开展新品种的技术研发和生产试验；发展公司＋农户的经营模式，带动周边农村食用菌产业的发展，形成是一个集科研、技术培训、开发、加工为一体的现代化高效环保、高科技生产的贸易型企业。</span></span></span></p>', '', '', '', '', '1399877300', '50', '1', '3');
INSERT INTO `fjznny_article` VALUES ('108', '26', '天津绿圣蓬源农业科技开发有限公司', '', '', '', '', '', '天津绿圣蓬源农业科技开发有限公司', '<p style=\"text-align: center; \">&nbsp;<img src=\"/upfiles/contentfiles/image/201405/20140512150404186.jpg\" width=\"600\" height=\"450\" alt=\"\" /></p>\r\n<p style=\"text-align: center; \"><span style=\"font-size: 14px; \"><span style=\"font-family: 宋体; \"><br type=\"_moz\" />\r\n</span></span></p>\r\n<p style=\"text-align: left; \">\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;line-height:18.0pt;background:white\"><span style=\"font-size: 14px; \"><span style=\"font-family: 宋体; \"><span lang=\"EN-US\" style=\"color: rgb(102, 102, 102); \">&nbsp; &nbsp;</span><span style=\"color: rgb(102, 102, 102); \">天津绿圣蓬源农业科技开发有限公司坐落在北辰区双街现代农业科技园内，公司以工厂化生产蟹味菇为主，是一个集科研、技术培训、开发、加工为一体的现代化高效环保、高科技生产的贸易型企业。</span></span></span><span lang=\"EN-US\" style=\"font-family:&quot;Verdana&quot;,&quot;sans-serif&quot;;color:#666666\"><o:p></o:p></span></p>\r\n<p style=\"margin: 0cm 0cm 0.0001pt; line-height: 18pt; background-color: white; background-position: initial initial; background-repeat: initial initial; \"><span style=\"font-size: 14px; \"><span style=\"font-family: 宋体; \"><span lang=\"EN-US\" style=\"color: rgb(102, 102, 102); \">&nbsp;&nbsp;&nbsp;</span><span style=\"color: rgb(99, 99, 99); background-color: white; \">公司总投资<span lang=\"EN-US\">1.1</span>亿元，总建筑面积<span lang=\"EN-US\">35000</span>㎡，生产设备、生产所用菌种及菌种培养技术均从日本引进，</span><span style=\"color: rgb(102, 102, 102); \">公司采取先进生产设备及生产技术，从菌种培养到包装贮藏整个过程全部使用自动化设备及机械手完成，</span><span style=\"color: rgb(99, 99, 99); background-color: white; \">年产量<span lang=\"EN-US\">7000</span>吨，实现年销售收入<span lang=\"EN-US\">1.4</span>亿元，利润在<span lang=\"EN-US\">30%</span>左右，</span><span style=\"color: rgb(102, 102, 102); \">产品主要销售京津地区，远销欧美市场。公司通过推广及技术合作，掌握国内一流的蟹味菇栽培生产技术和先进的管理理念，增强公司的整体实力，提升市场竞争力，生产技术达到国际领先水平，生产规模将排在国内前列。</span></span></span><span lang=\"EN-US\" style=\"font-family:&quot;Verdana&quot;,&quot;sans-serif&quot;;color:#666666\"><o:p></o:p></span></p>\r\n<p style=\"margin: 0cm 0cm 0.0001pt; line-height: 18pt; background-color: white; background-position: initial initial; background-repeat: initial initial; \"><span style=\"font-size: 14px; \"><span style=\"font-family: 宋体; \"><span lang=\"EN-US\" style=\"color: rgb(102, 102, 102); \">&nbsp;&nbsp;&nbsp; </span><span style=\"color: rgb(102, 102, 102); \">公司倡导社会责任意识，积极探索出菇后基质循环利用技术。为当地设施蔬菜种植基地改良土壤提供原料，同时收购周边地区的玉米芯、麸皮、棉花秆等农业秸秆资源，带动农户致富。公司计划利用现有优良设施条件和技术力量，生产各类别、各级别菌种，提供给周边菇农，带动菇农致富。逐步实现公司加农户的方式，发挥企业核心竞争力，在市场开拓、生产组织、质量控制、产品收购、价格调节上发挥作用，成为我市食用菌行业龙头企业。</span></span></span><span lang=\"EN-US\" style=\"font-family:&quot;Verdana&quot;,&quot;sans-serif&quot;;color:#666666\"><o:p></o:p></span></p>\r\n<span style=\"font-size: 14px; \"><span style=\"font-family: 宋体; \">  <span lang=\"EN-US\" style=\"color: rgb(102, 102, 102); \">&nbsp;&nbsp;&nbsp; </span><span style=\"color: rgb(102, 102, 102); \">公司在稳定和提高现有主打产品基础上，依托自身的人才储备和硬件设施，将开展新品种的技术研发和生产试验；发展公司＋农户的经营模式，带动周边农村食用菌产业的发展，形成是一个集科研、技术培训、开发、加工为一体的现代化高效环保、高科技生产的贸易型企业。</span></span></span></p>', '', '', '', '', '1399878217', '51', '1', '5');

