INSERT INTO `fjznny_downloads` VALUES ('9', '实盘交易软件', 'dl1.jpg', 'uploads/downloads/%E9%92%B1%E9%9A%86%E4%BC%9F%E4%B8%9A%E5%AE%9E%E7%9B%98%E4%BA%A4%E6%98%93%E8%BD%AF%E4%BB%B6.rar', '所有正式、模拟交易客户', '<p><span>功能强大、操作简单的网上交易系统，能够提供实时行情、网上交易及查询、报表功能，同时有  与银行接口的实时转账功能，方便客户出入金，让您的投资交易得到可靠保障，充分把握市场投资机会。模拟环境为全仿真交易环境，提供虚拟资金，让您在实际交 易前先熟悉电 子平台交易程序。 </span></p>', '1398276625', '999', '0', '0');
INSERT INTO `fjznny_downloads` VALUES ('10', '行情分析软件', 'dl2.jpg', 'uploads/downloads/%E9%92%B1%E9%9A%86%E4%BC%9F%E4%B8%9A%E5%AE%9E%E7%9B%98%E4%BA%A4%E6%98%93%E8%BD%AF%E4%BB%B6.rar', '所有交易客户', '<p><span>行情分析软件集行情与分析、信息资讯和交易为一体，提供国内外贵金属市场、外汇、期货市场行情及世界各大交易所实时行情指数，信息丰富全面、交易下单方便快捷。同时独具特色的每日行情分析与专家在线顾问服务，为广大投资者提供更多的专业支持。</span></p>', '1398276625', '998', '0', '0');
INSERT INTO `fjznny_downloads` VALUES ('11', '实盘模拟软件', 'dl3.jpg', 'uploads/downloads/%E9%92%B1%E9%9A%86%E4%BC%9F%E4%B8%9A%E5%AE%9E%E7%9B%98%E4%BA%A4%E6%98%93%E8%BD%AF%E4%BB%B6.rar', '所有交易客户', '<p><span>模拟账户是贵金属投资交易入门的最佳途径</span></p>', '1398276625', '997', '0', '0');
INSERT INTO `fjznny_downloads` VALUES ('12', '微软.Net Framewokrs', 'dl4.jpg', '', '所有交易客户', '<p><span>微软.Net Framewokrs 2.0/3.5。<br />\r\n</span></p>\r\n<p style=\"color:#9d1c30\">(注意：用户在安装过程中如果出现&quot;系统检测到您没有安装.Net Framewokrs 2.0/3.5,是否立 即下载并安装&quot;的情况,用户可以从这里下，请选择与操作系统相对应软件版本。)</p>\r\n<p>&nbsp;</p>\r\n<div class=\"dl\" style=\"width:230px; height:auto\"><a href=\"http://www.microsoft.com/zh-cn/download/confirmation.aspx?id=25150\" target=\"_blank\" style=\"width:230px; float:left\">Windows XP 32位&nbsp;下载</a><br />\r\n<a href=\"http://www.microsoft.com/zh-cn/download/confirmation.aspx?id=25150\" target=\"_blank\" style=\"width:230px; float:left\">Windows XP 64位&nbsp;下载</a><br />\r\n<a href=\"http://www.microsoft.com/zh-cn/download/confirmation.aspx?id=25150\" target=\"_blank\" style=\"width:230px; float:left\">Windows 7/Windows Vista&nbsp;下载</a></div>', '1398276625', '996', '0', '0');
INSERT INTO `fjznny_downloads` VALUES ('13', '微软 Windows Installer', 'dl5.jpg', 'uploads/downloads/%E9%92%B1%E9%9A%86%E4%BC%9F%E4%B8%9A%E5%AE%9E%E7%9B%98%E4%BA%A4%E6%98%93%E8%BD%AF%E4%BB%B6.rar', '所有交易客户', '<p><span>微软 Windows Installer 3.0。<br />\r\n<p style=\"color:#9d1c30\">(注意：用户在安装过程中如果出现&quot;错误,缺少系统必备软件.安装程序检测到未安装以下程 序:Microsoft Windows Installer 3.0&quot;的情况,用户可以从这里下载。)</p>\r\n</span></p>', '1398276625', '995', '0', '0');

